// Get the modal
var modal = document.getElementById('mModal');

// Get and set image in modal, get and set caption
var modalImg = document.getElementById("img");
var captionText = document.getElementById("caption");

// Get element that closes the modal and set to close
var span = document.getElementsByClassName("close")[0];
span.onclick = function() { 
  modal.style.display = "none";
}

// Get all image elements that need to be displayed via modal
var foodElements = document.getElementsByClassName('displayImg');
for (var i = 0; i < foodElements.length; i++) {
	foodElements[i].onclick = function(){
 		modal.style.display = "block";
  		modalImg.src = this.src;
  		captionText.innerHTML = this.alt;
	}
}